mapper.select:

SELECT * FROM SEAT
WHERE SCREEN_NO = #{SCREEN_NO}
ORDER BY SEAT_ROW ASC, SEAT_COL ASC

mapper.insert:

INSERT INTO TIME_SEAT
(
	SEAT_NO,
	TIME_NO
)
VALUES
(
	#{SEAT_NO},
	#{TIME_NO}	
)

mapper.update:
UPDATE SET TIME_SEAT
	STATUS = #{STATUS}
WHERE
	SEAT_NO = #{SEAT_NO} AND TIME_NO = #{TIME_NO}
